INSERT INTO product (productcode, subproductcode, version, defined_by, activated, category_id, product_type, descriptive_name, description, provider, frequency_id, date_format, scale_factor, scale_offset, nodata, mask_min, mask_max, unit, data_type_id, masked, timeseries_role) VALUES ('vgt-albe', 'err_bbdhr_vis', 'undefined', 'JRC', false, 'radiationflux', 'Ingest', 'SPOT/Vegetation Albedo Visible albedo Error', 'It corresponds to the amount of solar energy (spectrally integrated over visible spectral domain) reflected by a surface and provides information on the radiative balance, thus on temperature and water balance. The albedo is one of the most interesting variable required as a primary input for global circulation models used to forecast short term weather and long term climatic change. It is also mandatory for local to regional estimates of energy and mass exchanges between the earth surface and the atmosphere as described by soil vegetation atmosphere transfer models (SVAT).', 'VITO/DevCoCast', 'e1dekad', 'YYYYMMDD', 0, 0, 0, NULL, NULL, NULL, 'undefined', true, NULL);
